mapper.select:
SELECT
-- 级联表的字段 ,gequ.gequ_uuid_number as gequUuidNumber ,gequ.gequ_name as gequName ,gequ.gequ_types as gequTypes ,gequ.gequ_photo as gequPhoto ,gequ.gequ_geshou as gequGeshou ,gequ.gequ_music as gequMusic ,gequ.gequ_shizhang as gequShizhang ,gequ.zan_number as zanNumber ,gequ.cai_number as caiNumber ,gequ.gequ_content as gequContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM gequ_collection a left JOIN gequ gequ ON a.gequ_id = gequ.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.gequ_id = #{params.gequId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.gequ_collection_types = #{params.gequCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.gequ_id IS NOT NULL
and gequ.gequ_uuid_number like CONCAT('%',#{params.gequUuidNumber},'%')
and gequ.gequ_name like CONCAT('%',#{params.gequName},'%')
and gequ.gequ_types = #{params.gequTypes}
and gequ.gequ_geshou like CONCAT('%',#{params.gequGeshou},'%')
and gequ.gequ_music like CONCAT('%',#{params.gequMusic},'%')
and gequ.gequ_shizhang like CONCAT('%',#{params.gequShizhang},'%')
and gequ.zan_number >= #{params.zanNumberStart}
and gequ.zan_number <= #{params.zanNumberEnd}
and gequ.zan_number = #{params.zanNumber}
and gequ.cai_number >= #{params.caiNumberStart}
and gequ.cai_number <= #{params.caiNumberEnd}
and gequ.cai_number = #{params.caiNumber}
and gequ.gequ_content like CONCAT('%',#{params.gequContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
